import React, { Component } from 'react';
import { Card, CardImg, CardImgOverlay, CardText, CardBody,
    CardTitle } from 'reactstrap';
import {Menu} from './componentsMenuComponent';
import { Media } from 'reactstrap';

class DishDetails extends Component{
    constructor(props) {
        super(props);
    }
    renderCommentDish(dish)
    {  console.log(dish.comments.id);
        if(dish!=null)
        {
            return(
                <Media as="li">
                <li key={this.commen.id}>
               
                    {dish.comments.id}
                    {dish.comments.comment}
                   
               
                </li>
                </Media>
            );
        }
    }
    renderDish(dish) {
        if (dish != null)
            return(
                <Card>
                    <CardImg top src={dish.image} alt={dish.name} />
                    <CardBody>
                      <CardTitle>{dish.name}</CardTitle>
                      <CardText>{dish.description}</CardText>
                    </CardBody>
                </Card>
            );
        else
            return(
                <div></div>
            );
    }

render()
{
    return(
        <div className="row">
                  <div  className="col-12 col-md-5 m-1">
                    {this.renderDish(this.state.selectedDish)}
                  </div>
                  const com = this.props.dishes.map((commen) => {
                  <div  className="col-12 col-md-5 m-1">
                    {this.renderCommentDish(this.state.selectedDish)}
                  </div>
                  }
                </div>
                

    );
}

}
export default DishDetails;
